mapper.select:
select *
		from(select ROWNUM rn, st.*
		        FROM (select * from STATION_TB order by station_id) st)


		rn BETWEEN 1 AND #{endNo} 


		rn BETWEEN #{endNo}-9 AND #{endNo}

select *
		from(select ROWNUM rn, st.*
		        FROM (select * from STATION_TB where station_company=#{category} order by station_id) st)


		rn BETWEEN 1 AND #{endNo} 


		rn BETWEEN #{endNo}-9 AND #{endNo}
